DROP TABLE fh_tb_user;
drop table fh_tb_qna;
drop table fh_tb_free;
drop table fh_tb_notice;
drop table fh_tb_guestbook;

drop sequence idx_seq;
drop sequence qna_seq;
drop sequence free_seq;
drop sequence notice_seq;
drop sequence guestbook_seq;

create sequence idx_seq 
  start with 1 
  increment by 1 
  nomaxvalue;
  
create sequence notice_seq 
  start with 1 
  increment by 1 
  nomaxvalue;

create sequence qna_seq 
  start with 1 
  increment by 1 
  nomaxvalue;

create sequence free_seq
  start with 1
  increment by 1
  nomaxvalue
  nocache
  nocycle;
  
create sequence guestbook_seq
  start with 1
  increment by 1
  nomaxvalue
  nocache
  nocycle;
  
create table fh_tb_user (
  idx number not null,
  id varchar2(20) not null PRIMARY KEY,
  pwd varchar2(30) not null,
  phone varchar2(30),
  email varchar2(100),
  regdate timestamp not null
);

create table fh_tb_qna (
  idx number not null,
  id varchar2(20) not null,
  title varchar2(50) not null,
  content varchar2(2000),
  regdate timestamp not null
);

create table fh_tb_free (
  idx number not null,
  id varchar2(20) not null,
  title varchar2(50) not null,
  content varchar2(2000),
  regdate timestamp not null
);

create table fh_tb_notice (
  idx number not null,
  id varchar2(20) not null,
  title varchar2(50) not null,
  content varchar2(2000),
  regdate timestamp not null
);

create table fh_tb_guestbook (
  idx number not null,
  id varchar2(20) not null,
  pwd varchar2(30) not null,
  title varchar2(50) not null,
  content varchar2(2000),
  regdate timestamp not null
);






select * from fh_tb_user order by idx desc;
